    @Test
    public void getType2Json() throws IOException {
        WebRTCType type = new WebRTCType(WebRTCType.JOIN, 100L);
        ObjectMapper mapper = BeanFactory.getBean("mapper", ObjectMapper.class);
        String content = mapper.writeValueAsString(type);
        System.out.println(content);
        Map<String, Object> map = mapper.readValue(content, Map.class);
        System.out.println(map);
    }
